import React, { useState, useEffect,useRef } from "react";
import {useNavigate} from 'react-router-dom';
import axios from "axios";

const StudentPortal = () => {
  const navi = useNavigate()
  const [certificateId, setCertificateId] = useState("");
  const [studentName, setStudentName] = useState('');
  const [internshipDomain, setInternshipDomain] = useState('');
  const canvasRef = useRef(null);



  const generateCertificate = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.src = '/template.jpeg'; // Path to your template image

    img.onload = () => {
        // Set canvas size to match the image size
        canvas.width = img.width;
        canvas.height = img.height;

        // Draw the image on the canvas
        ctx.drawImage(img, 0, 0);

        // Set text style
        ctx.font = '30px Arial';  // Font style and size
        ctx.fillStyle = 'black';  // Text color
        ctx.textAlign = 'center';  // Center text alignment

        // Calculate positions based on the canvas size
        const studentNameX = canvas.width / 2; // X-coordinate for student name
        const studentNameY = 600; // Y-coordinate for student name
        const courseNameX = canvas.width / 2; // X-coordinate for course name
        const courseNameY = 800; // Y-coordinate for course name

        // Draw student name
        ctx.fillText(studentName, studentNameX, studentNameY);
        // Draw course name
        ctx.fillText(internshipDomain, courseNameX, courseNameY);
    };

    img.onerror = (error) => {
        console.error('Image failed to load:', error);
    };
};



  const handleSearch =async () => {
    const a = "http://localhost:3010/certificate/"+certificateId
    const result = await axios.get(a)
      const data = result.data.data
      await setStudentName(data.studentName)
      await setInternshipDomain( data.internshipDomain)
      console.log(studentName,internshipDomain)
  };

   
const downloadCertificate = () => {
  
    const canvas = canvasRef.current;
    const link = document.createElement('a');
    link.href = canvas.toDataURL('image/png'); // Convert canvas to image URL
    link.download = 'certificate.png'; // File name for download
    link.click(); // Trigger download
};


  return (
    <div>
     
      <h1>Student Portal</h1>
      <input
        type="text"
        value={certificateId}
        onChange={(event) => setCertificateId(event.target.value)}
      />
      <button onClick={handleSearch}>Search for Certificate</button>
      <form onSubmit={(e)=>{
          e.preventDefault(); // Prevent form submission
          
          generateCertificate()
      }}>
      <button type="submit">Generate Certificate</button>
      </form>
      
      {studentName && (
        <div>
          <h2>Certificate Details</h2>
          <p>Student Name: {studentName}</p>
          <p>Internship Domain: {internshipDomain}</p>
          
          <button onClick={downloadCertificate}>Download Certificate</button>
          <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
          
        </div>
      )}
    </div>
  );
};

export default StudentPortal;
