import React, { useState, useRef } from 'react';

function CertificateGenerator() {
    // State to hold student and course names
    const [studentName, setStudentName] = useState('');
    const [courseName, setCourseName] = useState('');

    // Reference to the canvas element
    const canvasRef = useRef(null);

    // Function to generate the certificate
    const generateCertificate = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        // Load the certificate template image
        const img = new Image();
        img.src = '/template.jpeg';  // Path to your template image

        img.onload = () => {
            // Set canvas size to match the image size
            canvas.width = img.width;
            canvas.height = img.height;

            // Draw the image on canvas
            ctx.drawImage(img, 0, 0);

            // Draw student details on the canvas
            ctx.font = '30px Arial';  // Font style and size
            ctx.fillStyle = 'black';  // Text color
            ctx.textAlign = 'center';  // Center text alignment

            // Adjust these coordinates based on your template
            ctx.fillText(studentName, canvas.width / 2, 300); // Adjust Y-coordinate as needed
            ctx.fillText(courseName, canvas.width / 2, 400);  // Adjust Y-coordinate as needed
        };

        img.onerror = (error) => {
            console.error('Image failed to load:', error);
        };
    };

    // Function to download the certificate
    const downloadCertificate = () => {
        const canvas = canvasRef.current;
        const link = document.createElement('a');
        link.href = canvas.toDataURL('image/png'); // Convert canvas to image URL
        link.download = 'certificate.png'; // File name for download
        link.click(); // Trigger download
    };

    return (
        <div>
            <h1>Certificate Generator</h1>
            <form
                onSubmit={(e) => {
                    e.preventDefault(); // Prevent form submission
                    generateCertificate(); // Generate the certificate
                }}
            >
                <div>
                    <label>
                        Student Name:
                        <input
                            type="text"
                            value={studentName}
                            onChange={(e) => setStudentName(e.target.value)}
                            required
                        />
                    </label>
                </div>
                <div>
                    <label>
                        Course Name:
                        <input
                            type="text"
                            value={courseName}
                            onChange={(e) => setCourseName(e.target.value)}
                            required
                        />
                    </label>
                </div>
                <button type="submit">Generate Certificate</button>
            </form>
            <button onClick={downloadCertificate}>Download Certificate</button>
            <canvas ref={canvasRef} style={{ display: 'none' }}></canvas>
        </div>
    );
}

export default CertificateGenerator;
