import React, { useState, useEffect } from "react";
import axios from "axios";

const StudentPortal = () => {
  return (
    <div>
        <h1>Student Portal</h1>
        <nav>
            <Link to="/certificate-generator">Generate My Certificate</Link>
            <br />
            <Link to="/admin">Back to Admin Dashboard</Link>
        </nav>
    </div>
);

  const [certificateId, setCertificateId] = useState("");
  const [certificate, setCertificate] = useState(null);

  const handleSearch = () => {
    const a = "http://localhost:3010/certificate/"+certificateId
    axios
      .get(a)
      .then((response) => {
        setCertificate(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Student Portal</h1>
      <input
        type="text"
        value={certificateId}
        onChange={(event) => setCertificateId(event.target.value)}
      />
      <button onClick={handleSearch}>Search for Certificate</button>
      {certificate && (
        <div>
          <h2>Certificate Details</h2>
          <p>Student Name: {certificate.studentName}</p>
          <p>Internship Domain: {certificate.internshipDomain}</p>
          <p>Start Date: {certificate.startDate}</p>
          <p>End Date: {certificate.endDate}</p>
          <button
            onClick={() =>
              window.open(
                `http://localhost:3010/certificate/${certificateId}/download`,
                "_blank"
              )
            }
          >
            Download Certificate
          </button>
        </div>
      )}
    </div>
  );
};

export default StudentPortal;
