import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Adminlogin(){
    const[username,setUsername] = useState("");
    const[pass,setPass] = useState("");
    async function handler(e){
        e.preventDefault();

        const formdata = new FormData();
        formdata.append("adminUsername",username)
        formdata.append("pass",pass)
        const result = await axios.post("http://localhost:3010/admincheck",formdata)
        const a = result.data.data
        if(a){
            console.log(a)
            alert("logged in successfully")
            navi("/admin")
        }
        else{
            alert("try again")
            navi("/admin-auth")
        }
    }
    return(
        <>
        <form onSubmit={handler}>
        <input type="text" onChange={(e)=>setUsername(e.target.value)} placeholder="enter admin username"></input>
        <input type="password" onChange={(e)=>setPass(e.target.value)} placeholder="enter admin pass"></input>
        <input type="submit"></input>
        </form>
        </>
    )
};
